package com.abdurashidov.myapplication.controllerimport com.abdurashidov.myapplication.model.ModelModuleimport com.abdurashidov.myapplication.view.LoginViewclass LoginController {    private lateinit var view: LoginView    fun bind(loginView: LoginView){        view=loginView    }    fun onLoginButtonClicked(){        ModelModule.dataAccessLayer.performLogin(view.getUserName(), view.getPassword())    }}